import Share from '../share/Share'
import Post from '../post/Post'
import './feed.css'
import {Posts} from "../../dummyData"
import { useEffect,useState } from 'react'
import axios from "axios"
export default function Feed() {
    //proxy設定
    axios.defaults.baseURL=process.env.REACT_APP_API

    //暫時使用此方式導入jwt Token
    const config = {
        headers: { Authorization: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MGVjZGU1ZjY4OGEyMDZjOTRjZDYwYmIiLCJ1c2VybmFtZSI6Im5ld3Rlc3QwOCIsImV4cCI6MTYyNjc0MDk2MC4xMSwiaWF0IjoxNjI2MTM2MTYwfQ.LHc2--Be24MLo-J03zDmjjt5vBWOYsUHpZs1dC1f8a8" }
    };
    const [posts,setPosts] = useState([]);

    useEffect(()=>{
        //這邊要用jwt token去呼叫
        //先隨機給一個 因為還沒拉好認證系統
        const fetchPosts = async ()=>{
            const res = await axios.get(
                "/api/posts/timeline/all",
                config
            );
            setPosts(res.data)
        };
        fetchPosts();
    },[]) //只render一次

    return (
        <div className="feed">
            <div className="feedWrapper">
                <Share/>
                {posts.map((p)=>(
                    <Post key={p.id} post={p}/>
                ))}
            </div>
        </div>
    )
}
