import Share from '../share/Share'
import Post from '../post/Post'
import './feed.css'
import { useContext, useEffect,useState } from 'react'
import axios from "axios"
import { AuthContext } from '../../context/AuthContext'
import { decodeToken } from 'react-jwt'
export default function Feed({username}) {
    //proxy設定
    //暫時使用此方式導入jwt Token
    console.log(username)
    const [posts,setPosts] = useState([]);
    const {user} = useContext(AuthContext);
    useEffect(()=>{
        //這邊要用jwt token去呼叫
        //先隨機給一個 因為還沒拉好認證系統
        const fetchPosts = async ()=>{
            const config = {
                headers: { Authorization:"Bearer "+user }
            };
            const token =  decodeToken(user)
            const res = username 
             ? await axios.get("/api/posts/profile/"+ token.username,config)
             : await axios.get("/api/posts/timeline/all",config)


            console.log(res.data.data)

            //格式不一樣，對格式做調整
            let data = ""
            username ? data = res.data.data[0] : data = res.data.data[0]
            setPosts(data)

        };
        fetchPosts();
    },[username,user]) //只render一次

    return (
        <div className="feed">
            <div className="feedWrapper">
                <Share/>
                {posts.map((p)=>(
                    //console.log(p.id)
                    <Post key={p._id} post={p}/>
                ))}
            </div>
        </div>
    )
}
