import { useRef } from "react";
import "./register.css"

export default function Register() {
    const username = useRef();
    const email = useRef();
    const password = useRef();
    const passwordAgain = useRef();

    const handleClick = (e) =>{
        e.preventDefault();
        if(passwordAgain.current.value !== password.current.value){
            password.current.setCustomValidity("密碼輸入不一致")
        }
    }
    return (
        <div className="login">
            <div className="loginWrapper">
                <div className="loginLeft">
                    <h3 className="loginLogo">HardCo.Social</h3>
                    <span className="loginDesc">
                        和朋友以及周圍的世界在HardCo.Social上建立連結吧.
                    </span>
                </div>
                <div className="loginRight">
                    <form className="loginBox" onSubmit={handleClick}>
                        <input placeholder="用戶名稱" 
                               ref={username}
                               className="loginInput"
                               required 
                        />
                        <input placeholder="電子郵件" 
                               ref={email} 
                               className="loginInput" 
                               required
                               type="email"
                        />
                        <input placeholder="密碼"    
                               ref ={password} 
                               className="loginInput" 
                               type="password"
                               required
                               minLength="6"
                        />
                        <input placeholder="再一次輸入密碼"  
                               ref={passwordAgain}
                               className="loginInput" 
                               type="password"
                               required

                        />
                        <button className="loginButton" type="submit">註冊</button>
                        <button className="loginRegisterButton">
                            去登入
                        </button>
                    </form>
                </div>
            </div>
        </div>
    )
}
