import "./topbar.css"
import { Search,Person,Chat,Notifications} from "@material-ui/icons"
import { Link } from "react-router-dom"
import { useContext,useEffect, useState } from "react"
import { AuthContext } from "../../context/AuthContext"
import { decodeToken } from 'react-jwt'
import axios from "axios"
export default function Topbar(){
    const [member,setMember] = useState({});
    const {token} = useContext(AuthContext) //jwt Token
    const PF = process.env.REACT_APP_PUBLIC_FOLDER
    //console.log(user)
    useEffect(()=>{
        //這邊要用jwt token去呼叫
        //先隨機給一個 因為還沒拉好認證系統
        // const config = {
        //     headers: { Authorization:"Bearer "+token }
        // };
        const user =  decodeToken(token)
        const fetchUser = async ()=>{
            const res = await axios.get(
                `/api/users?userId=${user._id}`,
                // config
            );
            //console.log(res.data.data)
            setMember(res.data.data)
        };
        fetchUser();
    },[token])

    //console.log(member.city)
    //透過user token在對遠端做要求

    //此處做切版
    return(
        <div className="topbarContainer">
            <div className="topbarLeft">
                <Link to="/" style={{textDecoration:"none"}}>
                <span className="logo">HardCo.Social</span>
                </Link>
            </div>
            <div className="topbarCenter">
                <div className="searchbar">
                    <Search className="searchIcon" />
                    <input placeholder="尋找朋友、貼文或影片" className="searchInput"/>
                </div>
            </div>
            <div className="topbarRight">
                <div className="topbarLinks">
                    <span className="topbarLink">Homepage</span>
                    <span className="topbarLink">Timeline</span>
                </div>
                <div className="topbarIcons">
                    <div className="topbarIconItem">
                        <Person/>
                        <span className="topbarIconBadge">1</span>
                    </div>
                    <div className="topbarIconItem">
                        <Chat/>
                        <span className="topbarIconBadge">1</span>
                    </div>
                    <div className="topbarIconItem">
                        <Notifications/>
                        <span className="topbarIconBadge">1</span>
                    </div> 
                </div>
                <Link to={`/profile/${member.username}`}> 
                <img src={member.profilePicture ? PF+member.profilePicture : PF+"noAvatar.png"} alt="" className="topbarImg" />
                </Link>
            </div>
        </div>
    )
}
